INSERT INTO [dbo].[Categories] values (@id, '2.15.2', 'Оператор по переводу денежных средств, оператор платежной системы, оператор услуг платежной инфраструктуры обеспечивают проведение оценки соответствия не реже одного раза в два года, а также по требованию Банка России.');
INSERT INTO [dbo].[Requirements] values ('Оператор по переводу денежных средств, оператор платежной системы, оператор услуг платежной инфраструктуры обеспечивают проведение оценки соответствия не реже одного раза в два года, а также по требованию Банка России', @id, @Cat3);
